  return (
  <div>
    <div>
      {post.title}
    </div>
    <div>
      <p>{post.description}</p>
    </div>
    <div>
      <img src={post.image} alt="" />
    </div>
    <div>
      <p>{post.price}</p>
    </div>
    <button>
      {
        cart.some((p)=> (p.id == post.id) ? (
          <button onClick={removeFromCart}>
            Remove Item
          </button>
        ) :(
          <button onClick={addToCart}>
            Add to Cart
          </button>
        )) ? <p>Remove Item</p> : <p>Add To Cart</p>
      }
    </button>
  </div>
);
};

export default Product;
